static char (*cadrans[4])[2] = {(char (*)[2])trois, (char (*)[2])trois, (char (*)[2])trois, (char (*)[2])trois};
int abs(int v){
  return (v >= 0)?v:-1*v;
}


char (*sym_to_a(char sym))[2]{
  switch (sym){
  case '0':
    return (char (*)[2]) zero;
  case '1':
    return (char (*)[2]) un;
  case '2':
    return (char (*)[2]) deux;
  case '3':
    return (char (*)[2]) trois;
  case '4':
    return (char (*)[2]) quatre;
  case '5':
    return (char (*)[2]) cinq;
  case '6':
    return (char (*)[2]) six;
  case '7':
    return (char (*)[2]) sept;
  case '8':
    return (char (*)[2]) huit;
  case '9':
  return (char (*)[2]) neuf;
    }
  return 0;
}

// 01:34
void update(char * t){
  if (time_has_changed()){
    cadrans[0] = sym_to_a(t[3]);
    cadrans[1] = sym_to_a(t[0]);
    cadrans[2] = sym_to_a(t[1]);
    cadrans[3] = sym_to_a(t[4]);
  }
}

void get_led_val(){  
  int alpha = get_current_angle();
  if (alpha == -1) return;
  if (last_angle != alpha){
    last_angle = alpha;
  }
  else
    return;
  alpha *= -1;
  alpha -= (90-RETARD); // led vers le haut par d√©faut
  while (alpha < 0)
    alpha = 360 + alpha;
  set_leds(cadrans[alpha/90][alpha/DIVISOR][0]
	   , cadrans[0][alpha/DIVISOR][1]);
}

void draw(){
  get_led_val();
}
